// Movies data with 25 movies
const movies = [
  { name: "The Dark Knight", imdbRating: 9.0, trailerUrl: "https://www.youtube.com/embed/EXeTwQWrcwY", description: "A superhero film directed by Christopher Nolan.", characters: ["Bruce Wayne", "Joker", "Rachel Dawes"] },
  { name: "Inception", imdbRating: 8.8, trailerUrl: "https://www.youtube.com/embed/YoHD9XEInc0", description: "A mind-bending thriller by Christopher Nolan.", characters: ["Dom Cobb", "Robert Fischer", "Mal"] },
  { name: "Interstellar", imdbRating: 8.6, trailerUrl: "https://www.youtube.com/embed/zSWdZVtmtYQ", description: "A space exploration film directed by Christopher Nolan.", characters: ["Cooper", "Brand", "Murph"] },
  { name: "The Matrix", imdbRating: 8.7, trailerUrl: "https://www.youtube.com/embed/vKQi3bBA1y8", description: "A hacker discovers a dystopian world controlled by machines.", characters: ["Neo", "Trinity", "Morpheus"] },
  { name: "The Shawshank Redemption", imdbRating: 9.3, trailerUrl: "https://www.youtube.com/embed/6hB3S9bIaco", description: "Two imprisoned men form a bond over several years.", characters: ["Andy Dufresne", "Ellis Boyd Redding"] },
  { name: "The Godfather", imdbRating: 9.2, trailerUrl: "https://www.youtube.com/embed/sY1S34973zA", description: "The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.", characters: ["Vito Corleone", "Michael Corleone", "Sonny Corleone"] },
  { name: "Pulp Fiction", imdbRating: 8.9, trailerUrl: "https://www.youtube.com/embed/s7EdQ4FqbhY", description: "The lives of two mob hitmen, a boxer, a gangster's wife, and a pair of diner bandits intertwine.", characters: ["Vincent Vega", "Jules Winnfield", "Mia Wallace"] },
  { name: "Fight Club", imdbRating: 8.8, trailerUrl: "https://www.youtube.com/embed/qtRKdVHc-cE", description: "An insomniac office worker and a soap salesman form an underground fight club.", characters: ["Tyler Durden", "Narrator", "Marla Singer"] },
  { name: "Forrest Gump", imdbRating: 8.8, trailerUrl: "https://www.youtube.com/embed/bLvqoHBptjg", description: "The presidencies of Kennedy and Johnson, the Vietnam War, the Watergate scandal, and other historical events unfold from the perspective of an Alabama man with an IQ of 75.", characters: ["Forrest Gump", "Jenny Curran"] },
  { name: "The Lord of the Rings: The Return of the King", imdbRating: 8.9, trailerUrl: "https://www.youtube.com/embed/r5X-hFf6Bwo", description: "Gondor's forces mount a siege in the battle for Middle-earth.", characters: ["Frodo Baggins", "Samwise Gamgee", "Gollum"] },
  { name: "The Empire Strikes Back", imdbRating: 8.7, trailerUrl: "https://www.youtube.com/embed/Vj1eWmJpmK4", description: "After the Rebels are brutally overpowered by the Empire, Luke Skywalker begins his training as a Jedi Knight while his friends are pursued across the galaxy.", characters: ["Luke Skywalker", "Han Solo", "Princess Leia"] },
  { name: "The Lion King", imdbRating: 8.5, trailerUrl: "https://www.youtube.com/embed/4sj1fG1g8I0", description: "Lion prince Simba and his father are targeted by his bitter uncle, who wants to ascend the throne himself.", characters: ["Simba", "Mufasa", "Scar"] },
  { name: "Gladiator", imdbRating: 8.5, trailerUrl: "https://www.youtube.com/embed/olP_Go1xOpw", description: "A former Roman General sets out to exact vengeance against the corrupt emperor who murdered his family.", characters: ["Maximus", "Commodus", "Lucilla"] },
  { name: "Avatar", imdbRating: 7.8, trailerUrl: "https://www.youtube.com/embed/5PSNL1qE6VY", description: "A paraplegic Marine dispatched to the moon Pandora on a unique mission becomes torn between following his orders and protecting the world he feels is his home.", characters: ["Jake Sully", "Neytiri", "Dr. Grace Augustine"] },
  { name: "Schindler's List", imdbRating: 9.0, trailerUrl: "https://www.youtube.com/embed/gG22Xkv5t8k", description: "In German-occupied Poland during World War II, Oskar Schindler gradually becomes concerned for his Jewish workers.", characters: ["Oskar Schindler", "Itzhak Stern", "Amon Goeth"] },
  { name: "The Prestige", imdbRating: 8.5, trailerUrl: "https://www.youtube.com/embed/o4gHCmTQDVI", description: "Two magicians engage in a bitter rivalry, trying to best each other with the ultimate illusion.", characters: ["Robert Angier", "Alfred Borden", "Cutter"] },
  { name: "Memento", imdbRating: 8.4, trailerUrl: "https://www.youtube.com/embed/wv6xfQm8WsI", description: "A man suffering from short-term memory loss attempts to solve his wife's murder.", characters: ["Leonard Shelby", "Natalie", "Teddy"] },
  { name: "The Departed", imdbRating: 8.5, trailerUrl: "https://www.youtube.com/embed/SGWcnQFJY3s", description: "An undercover cop and a mole in the police attempt to identify each other while infiltrating an Irish gang in Boston.", characters: ["Billy Costigan", "Colin Sullivan", "Frank Costello"] },
  { name: "The Social Network", imdbRating: 8.0, trailerUrl: "https://www.youtube.com/embed/lB95KLmpLR4", description: "A story about the founding of Facebook, and the lawsuits that followed.", characters: ["Mark Zuckerberg", "Eduardo Saverin", "Sean Parker"] },
  { name: "The Wolf of Wall Street", imdbRating: 8.2, trailerUrl: "https://www.youtube.com/embed/iszwuX1AK6A", description: "Jordan Belfort's rise and fall on Wall Street.", characters: ["Jordan Belfort", "Donnie Azoff", "Naomi Lapaglia"] },
  { name: "The Incredibles", imdbRating: 8.0, trailerUrl: "https://www.youtube.com/embed/JQZMj3Bxz6s", description: "A family of superheroes comes together to fight a villain who is out to destroy them.", characters: ["Mr. Incredible", "Elastigirl", "Dash"] },
  { name: "The Hunger Games", imdbRating: 7.2, trailerUrl: "https://www.youtube.com/embed/PbM9hVtzy8E", description: "In a dystopian future, a young girl is forced to compete in a deadly televised game where she must fight for her survival.", characters: ["Katniss Everdeen", "Peeta Mellark", "Gale Hawthorne"] },
  { name: "12 Angry Men", imdbRating: 9.0, trailerUrl: "https://www.youtube.com/embed/_13J_3A4p8A", description: "A jury holds the fate of a young man accused of murder. They must deliberate the case thoroughly before making a decision.", characters: ["Juror 8", "Juror 3", "Juror 10"] },
  { name: "Citizen Kane", imdbRating: 8.3, trailerUrl: "https://www.youtube.com/embed/9d7V9fJzZjc", description: "The story of the life of a publishing tycoon, told in flashbacks after his death.", characters: ["Charles Foster Kane", "Susan Alexander"] },
  { name: "The Big Lebowski", imdbRating: 8.1, trailerUrl: "https://www.youtube.com/embed/9qV8lTcY9c8", description: "The Dude is mistaken for a millionaire and becomes embroiled in a case of mistaken identity and chaos.", characters: ["The Dude", "Walter Sobchak", "Donny"] }
];

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [hoveredMovie, setHoveredMovie] = useState(null);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  const closeModal = () => {
    setSelectedMovie(null);
  };

  return (
    <div className="dashboard">
      <h2>Movies Dashboard</h2>
      <div className="movie-list">
        {movies.map((movie, index) => (
          <div
            key={index}
            className="movie-item"
            onClick={() => handleMovieClick(movie)}
            onMouseEnter={() => setHoveredMovie(movie)}
            onMouseLeave={() => setHoveredMovie(null)}
          >
            <h3>{movie.name}</h3>
            <div className="rating">
              {hoveredMovie?.name === movie.name && (
                <span>IMDb Rating: {movie.imdbRating}</span>
              )}
            </div>
          </div>
        ))}
      </div>

      {selectedMovie && (
        <div className="movie-modal">
          <div className="modal-content">
            <button onClick={closeModal} className="close-btn">Close</button>
            <h2>{selectedMovie.name}</h2>
            <iframe
              src={selectedMovie.trailerUrl}
              title="Movie Trailer"
              width="100%"
              height="400px"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
            <p><strong>Description:</strong> {selectedMovie.description}</p>
            <p><strong>Characters:</strong> {selectedMovie.characters.join(", ")}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
