import React, { useState } from "react";

// 25 Movies Data (You can use real images for the `image` field)
const movies = [
  { name: "The Dark Knight", imdbRating: 9.0, trailerUrl: "https://www.youtube.com/embed/EXeTwQWrcwY", image: "https://via.placeholder.com/200x300?text=The+Dark+Knight", description: "A superhero film directed by Christopher Nolan.", characters: ["Bruce Wayne", "Joker", "Rachel Dawes"] },
  { name: "Inception", imdbRating: 8.8, trailerUrl: "https://www.youtube.com/embed/YoHD9XEInc0", image: "https://via.placeholder.com/200x300?text=Inception", description: "A mind-bending thriller by Christopher Nolan.", characters: ["Dom Cobb", "Robert Fischer", "Mal"] },
  { name: "Interstellar", imdbRating: 8.6, trailerUrl: "https://www.youtube.com/embed/zSWdZVtmtYQ", image: "https://via.placeholder.com/200x300?text=Interstellar", description: "A space exploration film directed by Christopher Nolan.", characters: ["Cooper", "Brand", "Murph"] },
  // Add 22 more movies here similarly
];

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [hoveredMovie, setHoveredMovie] = useState(null);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  const closeModal = () => {
    setSelectedMovie(null);
  };

  return (
    <div className="dashboard">
      <h2>Movies Dashboard</h2>
      <div className="movie-list">
        {movies.map((movie, index) => (
          <div
            key={index}
            className="movie-item"
            onClick={() => handleMovieClick(movie)}
            onMouseEnter={() => setHoveredMovie(movie)}
            onMouseLeave={() => setHoveredMovie(null)}
          >
            <img src={movie.image} alt={movie.name} />
            <h3>{movie.name}</h3>
            <div className="rating">
              {hoveredMovie?.name === movie.name && (
                <span>IMDb Rating: {movie.imdbRating}</span>
              )}
            </div>
          </div>
        ))}
      </div>

      {selectedMovie && (
        <div className="movie-modal">
          <div className="modal-content">
            <button onClick={closeModal} className="close-btn">X</button>
            <h2>{selectedMovie.name}</h2>
            <iframe
              src={selectedMovie.trailerUrl}
              title="Movie Trailer"
              allowFullScreen
            />
            <p><strong>Description:</strong> {selectedMovie.description}</p>
            <p><strong>Characters:</strong> {selectedMovie.characters.join(", ")}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
