import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate for redirection

// Movie data with 10 movies (replace with actual poster URLs and trailer URLs)
const movies = [
  {
    name: "The Dark Knight",
    imdbRating: 9.0,
    trailerUrl: "https://www.youtube.com/embed/EXeTwQWrcwY",
    description: "A superhero film directed by Christopher Nolan.",
    characters: ["Bruce Wayne", "Joker", "Rachel Dawes"],
    image: "https://m.media-amazon.com/images/I/81cHfEZ7-hL._AC_SY679_.jpg"
  },
  {
    name: "Inception",
    imdbRating: 8.8,
    trailerUrl: "https://www.youtube.com/embed/YoHD9XEInc0",
    description: "A mind-bending thriller by Christopher Nolan.",
    characters: ["Dom Cobb", "Robert Fischer", "Mal"],
    image: "https://m.media-amazon.com/images/I/71p5-Sv2+LL._SY679_.jpg"
  },
  {
    name: "The Matrix",
    imdbRating: 8.7,
    trailerUrl: "https://www.youtube.com/embed/m8e-FF8MsqU",
    description: "A hacker discovers a dystopian reality controlled by machines.",
    characters: ["Neo", "Morpheus", "Trinity"],
    image: "https://m.media-amazon.com/images/I/71VZKczIlwL._SY679_.jpg"
  },
  {
    name: "Interstellar",
    imdbRating: 8.6,
    trailerUrl: "https://www.youtube.com/embed/zSWdZVtXT7E",
    description: "A team of explorers travel through a wormhole in space.",
    characters: ["Cooper", "Dr. Brand", "Murph"],
    image: "https://m.media-amazon.com/images/I/81lG72z4s7L._AC_SY679_.jpg"
  },
  {
    name: "Avengers: Endgame",
    imdbRating: 8.4,
    trailerUrl: "https://www.youtube.com/embed/TcMBFSGVi1c",
    description: "The Avengers assemble to undo the damage caused by Thanos.",
    characters: ["Iron Man", "Captain America", "Thor"],
    image: "https://m.media-amazon.com/images/I/71zd5-MXwwL._AC_SY679_.jpg"
  },
  {
    name: "Jurassic Park",
    imdbRating: 8.1,
    trailerUrl: "https://www.youtube.com/embed/lc0UehYemI4",
    description: "A theme park with cloned dinosaurs turns into a nightmare.",
    characters: ["Dr. Alan Grant", "Dr. Ian Malcolm", "John Hammond"],
    image: "https://m.media-amazon.com/images/I/91fmiCfvA7L._AC_SY679_.jpg"
  },
  {
    name: "Titanic",
    imdbRating: 7.8,
    trailerUrl: "https://www.youtube.com/embed/kVrqfYjkTdQ",
    description: "A romance unfolds aboard the ill-fated Titanic ship.",
    characters: ["Jack Dawson", "Rose DeWitt Bukater", "Caledon Hockley"],
    image: "https://m.media-amazon.com/images/I/91J-W+kIQ7L._AC_SY679_.jpg"
  },
  {
    name: "The Lion King",
    imdbRating: 8.5,
    trailerUrl: "https://www.youtube.com/embed/4sj1MT05lAA",
    description: "A young lion prince flees his kingdom only to learn the true meaning of responsibility.",
    characters: ["Simba", "Mufasa", "Scar"],
    image: "https://m.media-amazon.com/images/I/91KkT2z4hzL._AC_SY679_.jpg"
  },
  {
    name: "The Godfather",
    imdbRating: 9.2,
    trailerUrl: "https://www.youtube.com/embed/sY1S34973zA",
    description: "The powerful story of a mafia family and its leader.",
    characters: ["Don Vito Corleone", "Michael Corleone", "Tom Hagen"],
    image: "https://m.media-amazon.com/images/I/91c5JwJ4r0L._AC_SY679_.jpg"
  },
  {
    name: "Pulp Fiction",
    imdbRating: 8.9,
    trailerUrl: "https://www.youtube.com/embed/s7EdQ4FqbhY",
    description: "A series of intertwining stories in Los Angeles.",
    characters: ["Vincent Vega", "Jules Winnfield", "Mia Wallace"],
    image: "https://m.media-amazon.com/images/I/81Aq4VZ4OgL._AC_SY679_.jpg"
  }
];

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const navigate = useNavigate();

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  const closeModal = () => {
    setSelectedMovie(null);
  };

  const handleLogout = () => {
    // Example logout logic
    // Clear session or authentication token
    // localStorage.removeItem('authToken');
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className="dashboard">
      <h2 className="title">Movies Dashboard</h2>

      {/* Logout Button */}
      <button onClick={handleLogout} className="logout-btn">Logout</button>

      <div className="movie-grid">
        {movies.map((movie, index) => (
          <div
            key={index}
            className="movie-item"
            onClick={() => handleMovieClick(movie)}
          >
            <img className="movie-poster" src={movie.image} alt={movie.name} />
            <div className="movie-info">
              <h3>{movie.name}</h3>
              <p>IMDb: {movie.imdbRating}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Movie Modal */}
      {selectedMovie && (
        <div className="movie-modal">
          <div className="modal-content">
            <button onClick={closeModal} className="close-btn">Close</button>
            <h2>{selectedMovie.name}</h2>
            <iframe
              src={selectedMovie.trailerUrl}
              title="Movie Trailer"
              width="100%"
              height="400px"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
            <p><strong>Description:</strong> {selectedMovie.description}</p>
            <p><strong>Characters:</strong> {selectedMovie.characters.join(", ")}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
