  { name: "The Departed", imdbRating: 8.5, trailerUrl: "https://www.youtube.com/embed/SGWcnQFJY3s", image: "https://via.placeholder.com/200x300?text=The+Departed", description: "An undercover cop and a mole in the police attempt to identify each other while infiltrating an Irish gang in Boston.", characters: ["Billy Costigan", "Colin Sullivan", "Frank Costello"] },
