  { name: "The Incredibles", imdbRating: 8.0, trailerUrl: "https://www.youtube.com/embed/JQZMj3Bxz6s", image: "https://via.placeholder.com/200x300?text=The+Incredibles", description: "A family of superheroes comes together to fight a villain who is out to destroy them.", characters: ["Mr. Incredible", "Elastigirl", "Dash"] },
  { name: "Jurassic Park", imdbRating: 8.1, trailerUrl: "https://www.youtube.com/embed/CrTZ6p5jNVs", image: "https://via.placeholder.com/200x300?text=Jurassic+Park", description: "During a preview tour, a theme park suffers a major power breakdown that allows its cloned dinosaur exhibits to run wild.", characters: ["Dr. Alan Grant", "Dr. Ellie Sattler", "John Hammond"] },
  { name: "Spider-Man: No Way Home", imdbRating: 8.3, trailerUrl: "https://www.youtube.com/embed/JfVOs4V2T2s", image: "https://via.placeholder.com/200x300?text=Spider-Man+No+Way+Home", description: "Peter Parker seeks the help of Doctor Strange after his identity is revealed.", characters: ["Peter Parker", "Doctor Strange", "MJ"] },
  { name: "Deadpool", imdbRating: 8.0, trailerUrl: "https://www.youtube.com/embed/INtNh1BfFpc", image: "https://via.placeholder.com/200x300?text=Deadpool", description: "A wisecracking mercenary gets experimented on, gaining extraordinary abilities.", characters: ["Wade Wilson", "Vanessa Carlysle", "Colossus"] }
];

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [hoveredMovie, setHoveredMovie] = useState(null);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  const closeModal = () => {
    setSelectedMovie(null);
  };

  return (
    <div className="dashboard">
      <h2>Movies Dashboard</h2>
      <div className="movie-list">
        {movies.map((movie, index) => (
          <div
            key={index}
            className="movie-item"
            onClick={() => handleMovieClick(movie)}
            onMouseEnter={() => setHoveredMovie(movie)}
            onMouseLeave={() => setHoveredMovie(null)}
          >
            <img src={movie.image} alt={movie.name} />
            <h3>{movie.name}</h3>
            <div className="rating">
              {hoveredMovie?.name === movie.name && (
                <span>IMDb Rating: {movie.imdbRating}</span>
              )}
            </div>
          </div>
        ))}
      </div>

      {selectedMovie && (
        <div className="movie-modal">
          <div className="modal-content">
            <button onClick={closeModal} className="close-btn">X</button>
            <h2>{selectedMovie.name}</h2>
            <iframe
              src={selectedMovie.trailerUrl}
              title="Movie Trailer"
              allowFullScreen
            />
            <p><strong>Description:</strong> {selectedMovie.description}</p>
            <p><strong>Characters:</strong> {selectedMovie.characters.join(", ")}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
